import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <NavContainer>
      <LogoContainer>
        <h2>TWOKAY CHEMICALS</h2>
        <img src='./images/Logo.png' alt='twokay logo' />
      </LogoContainer>
      <MobileMenu onClick={toggleNavbar}>
        <div className={`bar ${isOpen ? 'open' : ''}`}></div>
        <div className={`bar ${isOpen ? 'open' : ''}`}></div>
        <div className={`bar ${isOpen ? 'open' : ''}`}></div>
      </MobileMenu>
      <Nav className={isOpen ? 'open' : ''}>
        <NavMenu>
          <NavLink className="Link" to="/" activestyle><span>HOME</span></NavLink>
          <NavLink className="Link" to="/products" activestyle><span>PRODUCTS</span></NavLink>
          <NavLink className="Link" to="/contact" activestyle><span>CONTACT US</span></NavLink>
          <NavLink className="Link" to="/about" activestyle><span>ABOUT</span></NavLink>
        </NavMenu>
      </Nav>
    </NavContainer>
  );
};

const NavContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  position: absolute;
  top: 0;
  width: 100%;
`;

const LogoContainer = styled.div`
  padding: 0 0 0 40px;
  display: flex;
  flex-direction: row;
  height: 550px;
  background-color: rgba(45, 169, 92, 0.9);
  width: 40%;
  img {
    background: transparent;
    max-width: 100px;
    max-height: 100px;
  }
  h2 {
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 20px;
    color: white;
  }
`;

const MobileMenu = styled.div`
  display: flex;
  flex-direction: column;
  cursor: pointer;
  color: #333;
  position: absolute;
  top: 20px; /* Position at the top */
  right: 20px; /* Adjust positioning as needed */
  z-index: 1100; /* Ensure the mobile menu stays on top */
  .bar {
    width: 25px;
    height: 3px;
    background-color: #333;
    margin: 2px 0;
    transition: 0.2s;
  }
  &.open .bar:nth-child(1) {
    transform: rotate(-45deg) translate(4px, 6px);
  }
  &.open .bar:nth-child(2) {
    opacity: 0;
  }
  &.open .bar:nth-child(3) {
    transform: rotate(45deg) translate(4px, -6px);
  }
`;

const Nav = styled.div`
  display: flex;
  height: 85px;
  font-family: 'Poppins', sans-serif;
  font-weight: 500;
  font-size: 1.2rem;
  justify-content: space-between;
  padding: 20px 50px 50px 20px;
  z-index: 12;
  &.open {
    margin-top: 150px; /* Adjust the margin as needed */
  }
  @media screen and (max-width: 768px) {
    display: none;
  }
`;

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  margin-right: -24px;
  @media screen and (max-width: 768px) {
    display: none;
  }
  .Link {
    color: #033b4a;
    display: inline-block;
    align-items: center;
    text-decoration: none;
    padding: 0 1rem;
    height: 100%;
    cursor: pointer;
    &.active {
      color: #193874;
      span {
        border-bottom-style: solid;
        border-bottom-width: 2px;
        padding-bottom: 0.1rem;
        padding-top: 0.5rem;
        transition: width 0.3s ease-in-out;
      }
    }
  }
`;

export default Navbar;
