import React from "react";
import styled from 'styled-components';

const d = new Date();
let year = d.getFullYear();

const Footer = () => {

    return (
        <FooterContainer>
            <div className="flex-container">
            <SocialmediaContainer>
                <p>Twokay chemicals</p>
            </SocialmediaContainer>
            <AddressContainer>
                <ul>
                    <li>48 Kijabe street, Nairobi</li>
                    <li>Phone: +254 714 687727</li>
                    <li>P.O BOX 46169</li>
                    <li>twokay@bidii.com</li>
                </ul>
            </AddressContainer>
            <QuicklinksContainer>
                <ul>
                    <li></li>
                    <li>Contact</li>
                    <li>Our products</li>
                    <li></li>
                </ul>
            </QuicklinksContainer>
            </div>
            <CopyrightContainer>
            <p>Copyright Â© {year} Twokay chemicals limited. All rights reserved.</p>
            </CopyrightContainer>

        </FooterContainer>
    )
};

const FooterContainer = styled.div`

margin-top: 100px;

.flex-container{
    display:flex;
    flex-direction: row;
    justify-content: space-between;
    padding: 20px 200px 0 200px;
}


ul {
    list-style-type: none;
}


background-color: #21211f;
height: 300px;
width: 100vw;
color: #e5e4e2;
font-size: 16px;
font-weight: 200;
line-height: 27px;
font-color: rgb(109, 109, 109);

p {
    text-align: center; 
}

`
const SocialmediaContainer = styled.div`
display: flex;
justify-content: flex-start;
align-items: top;
margin: 10px 10px; 
min-height: 150px;
font-size: 40px;
width: 40%;


p{
    font-family: 'Allura', cursive;
}`
const AddressContainer = styled.div`
display: flex;
justify-content: flex-start;
align-items: top;
margin: 10px 10px; 
min-height: 150px;
width: 30%;
`
const QuicklinksContainer = styled.div`
display: flex;
justify-content: center;
align-items: top;
margin: 10px 10px; 
min-height: 150px;
width: 30%;
`
const CopyrightContainer = styled.div`

background-color: #0b7f6a;
color: #e5e4e2;


p{
    font-size: 14px;

}`
export default Footer;
