import React, { useState } from "react";
import { Link } from "react-router-dom";
import styled from 'styled-components';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNav = () => setIsOpen(!isOpen);

  const closeNav = () => setIsOpen(false); // Function to close the navigation menu

  return (
    <Nav className="navbar">
      <div className="navbar-brand">
        <Link to="/" className="navbar-item">
          Your Brand
        </Link>
        {/* Use a span for the hamburger icon */}
        <span
          className={`navbar-burger ${isOpen ? "is-active" : ""}`}
          aria-label="menu"
          aria-expanded="false"
          onClick={toggleNav}
        >
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </span>
      </div>
      {/* Render the navigation menu based on the isOpen state */}
      <div className={`navbar-menu ${isOpen ? "is-active" : ""}`}>
        <div className="navbar-start">
          <Link
            to="/"
            className={`navbar-item ${isOpen ? "is-visible" : ""}`}
            onClick={closeNav} // Close the menu when a link is clicked
          >
            Home
          </Link>
          <Link
            to="/about"
            className={`navbar-item ${isOpen ? "is-visible" : ""}`}
            onClick={closeNav}
          >
            About
          </Link>
          <Link
            to="/products"
            className={`navbar-item ${isOpen ? "is-visible" : ""}`}
            onClick={closeNav}
          >
            Products
          </Link>
          <Link
            to="/contact"
            className={`navbar-item ${isOpen ? "is-visible" : ""}`}
            onClick={closeNav}
          >
            Contact
          </Link>
        </div>
      </div>
    </Nav>
  );
};

export default Navbar;

const Nav = styled.div`

.navbar-item {
    color: #38ad6a;
}

/* Styling for the hamburger icon */
.navbar-burger {
  display: none; /* Hide by default */
  cursor: pointer;
  padding: 10px;
}

@media (max-width: 768px) { /* Show on mobile screens */
  .navbar-burger {
    display: inline-block;
  }
  
  /* Hide the navigation menu by default on mobile */
  .navbar-menu {
    display: none;
  }

  /* Toggle the navigation menu when the hamburger is clicked */
  .navbar-burger.is-active ~ .navbar-menu {
    display: block;
  }
  
  /* Show the navigation items when the hamburger is clicked */
  .is-visible {
    display: block;
  }
}

.bar {
  display: block;
  width: 25px;
  height: 3px;
  background-color: #38ad6a; /* Change the icon color */
  margin: 5px auto;
  transition: all 0.3s ease-in-out;
}

.navbar-burger.is-active .bar {
  background-color: #333; /* Change the color when active */
}`
