import React from 'react';
import styled from 'styled-components'
// import { FaBars } from "react-icons/fa";
// import {NavLink} from "react-router-dom";                
import { BrowserRouter as Router, Routes, Route,  } from 'react-router-dom';
import Home from '../pages/home';
import Product from '../pages/products';
import Contact from '../pages/contact';
import About from '../pages/about';


const Header = () => {
    return (
        <HeaderContainer>

        <HeaderImg>
            <img src='./images/header-pic2.jpg' alt='Header Image. Photo by <a href="https://unsplash.com/de/@towfiqu999999?utm_source=unsplash&utm_medium=referral&utm_content=creditCopyText">Towfiqu barbhuiya</a> on <a href="https://unsplash.com/photos/n-7HTOiJPso?utm_source=unsplash&utm_medium=referral&utm_content=creditCopyText">Unsplash</a>'/>
        </HeaderImg>

        <TopContainer>
            <NavbarContainer>
            <LogoContainer>
                <img src='./images/Logo.png' alt='twokay logo' />	
            </LogoContainer>
            <NavContainer>
                <Nav>
                    <NavMenu> 
                        <Router>
                            <Routes>
                                <Route className ="Link" path='./home' element={<Home/>}>HOME</Route>
                                <Route className ="Link" path='./products' element={<Product/>}>PRODUCTS</Route>
                                <Route className ="Link" path='./contact' element= {<Contact/>}>CONTACT</Route>
                                <Route className ="Link" path='./about' element = {<About />}>ABOUT</Route>
                            </Routes>
                        </Router>
                        {/* <NavLink className="Link" to="./home" activeStyle>HOME</NavLink>
                        <NavLink className="Link" to="./products" activeStyle>PRODUCTS</NavLink>
                        <NavLink className="Link" to="./contact" activeStyle>CONTACT</NavLink>
                        <NavLink className="Link" to="./blog" activeStyle>BLOG</NavLink>
                        <NavLink className="Link" to="./about" activeStyle>ABOUT</NavLink> */}
                    </NavMenu>
                </Nav>
            </NavContainer>
            </NavbarContainer>
        </TopContainer>
        
        </HeaderContainer>
    )
};

const HeaderContainer = styled.div`

`
const HeaderImg = styled.div`
max-width: 100vw;
height: 650px;

img {
    width:100%;
    height:100%;
    object-fit:cover;
}

`
const TopContainer = styled.div`
position: absolute;
top:0;
width: 100%;
`


const NavbarContainer = styled.div`
// display:flex;
// flex-direction: row;
// justify-content: space-between;
`


const Nav = styled.div`
display: flex;
height: 85px;
justify-content: space-between;
padding: 0.2rem calc((100vw - 1000px) / 2);
z-index: 12;
`

const NavMenu = styled.div`
  display: flex;
  background-color: black;
  height: 300px;
  width: 100%;
  color: white;

  align-items: center;
  margin-right: -24px;
  /* Second Nav */
  /* margin-right: 24px; */
  /* Third Nav */
  /* width: 100vw;
white-space: nowrap; */
  @media screen and (max-width: 768px) {
    display: none;
  }

.Link {
    color: white;
    display: flex;
    align-items: center;
    text-decoration: none;
    padding: 0 1rem;
    height: 100%;
    cursor: pointer;
    &.active {
      color: #4d4dff;
    }
}

`
