import React from 'react';
import styled from 'styled-components';

const Card = (props) => {
    return (
        <CardContainer>
            <img src= {props.img} alt={props.alt} />
            <h3>{props.title}</h3>
            <p>{props.content}</p>
        </CardContainer>
    )
};

const CardContainer = styled.div`

img {
    width: 30%;
    height: auto;
}

`

export default Card;
