import React from 'react';
import styled from 'styled-components'
import {NavLink} from "react-router-dom";                



const Header = () => {
    return (
        <HeaderContainer>

        <HeaderImg>
            <img src='./images/header-pic2.jpg' alt='Header'/>
        </HeaderImg>

        <TopContainer>
            <NavbarContainer>
            <LogoContainer>
                <h2>TWOKAY CHEMICALS</h2>
                <img src='./images/Logo.png' alt='twokay logo' />	
            </LogoContainer>
            <NavContainer>
                <Nav>
                    <NavMenu> 
                        <NavLink className="Link" to="/" activestyle>HOME</NavLink>
                        <NavLink className="Link" to="/products" activestyle>PRODUCTS</NavLink>
                        <NavLink className="Link" to="/contact" activestyle>CONTACT</NavLink>
                        <NavLink className="Link" to="/blog" activestyle>BLOG</NavLink>
                        <NavLink className="Link" to="/about" activestyle>ABOUT</NavLink>
                    </NavMenu>
                </Nav>
            </NavContainer>
            </NavbarContainer>
        </TopContainer>
        
        </HeaderContainer>
    )
};

const HeaderContainer = styled.div`

`
const HeaderImg = styled.div`
max-width: 100vw;
height: 700px;
opacity: 0.7;

img {
    width:100%;
    height:100%;
    object-fit:cover;
}

`
const TopContainer = styled.div`
position: absolute;
top:0;
width: 100%;

`


const NavbarContainer = styled.div`
display:flex;
flex-direction: row;
justify-content: space-between;`

const LogoContainer = styled.div`
padding: 0 0 0 40px;
display: flex;
flex-direction: row;
height: 700px;
background-color: rgb(45,169,92, .9);
width: 40%;
img {
    background: transparent;
    max-width: 100px;
    max-height: 100px;

}

h2{
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 20px;
    color:white;
}

`


const Nav = styled.div`
display: flex;
height: 85px;
font-family: 'Poppins', sans-serif;
font-weight: 500;
font-size: 1.2rem;
justify-content: space-between;
padding: 20px 50px 50px 20px;
z-index: 12;
`

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  margin-right: -24px;
  /* Second Nav */
  /* margin-right: 24px; */
  /* Third Nav */
  /* width: 100vw;
white-space: nowrap; */
  @media screen and (max-width: 768px) {
    display: none;
  }

.Link {
    color: #033B4A;
    display: flex;
    align-items: center;
    text-decoration: none;
    padding: 0 1rem;
    height: 100%;
    cursor: pointer;
    &.active {
      color: #193874;
      border-bottom-style: solid;
      border-bottom-width: 2px;
      text-underline-offset: auto;
      padding-bottom: 1rem;
      padding-top: 1rem;
      width: auto;
    }
}

`
const NavContainer = styled.div`

`

export default Header;
