import React from 'react';
import styled from 'styled-components'              

const Header = (props) => {
    return (

        <HeaderContainer>

        <HeaderIntro>
            <h1></h1>
            <p></p>

        </HeaderIntro>

        <HeaderImg className='image-container'>
            <img src={props.headerImage} alt='Header'/>
        </HeaderImg>

        </HeaderContainer>

        
    )
};

const HeaderContainer = styled.div`
margin-top: -22px;

`
const HeaderIntro = styled.div`

`

const HeaderImg = styled.div`
max-width: 100vw;
height: 400px;
position: relative;

// .image-container::before {
//     content: "";
//     position: absolute;
//     top: 0;
//     left: 0;
//     width: 100%;
//     height: 100%;
//     background-color: rgba(0, 0, 0, 0.5); /* Adjust the last value (0.5) for opacity */
//     z-index: 1;
//   }

// img {
//     width:100%;
//     height:100%;
//     object-fit:cover;
//     z-index: 2;
// }

.image-container {
    position: relative;
    width: 300px; /* Adjust to your image size */
    height: 200px; /* Adjust to your image size */
  }
  
  .image-container::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5); /* Adjust the last value (0.5) for opacity */
    z-index: 1;
  }
  
  .image-container img {
    width: 100%;
    height: 400px;
    object-fit: cover; /* Maintain aspect ratio and cover the container */
    z-index: 2;
  }
  

`

export default Header;
