import React from 'react';
import styled from 'styled-components'
// import { FaBars } from "react-icons/fa";
// import {NavLink} from "react-router-dom";                
import { BrowserRouter as Router, Routes, Route,  } from 'react-router-dom';
import Home from '../pages/home';
import Product from '../pages/products';
import Contact from '../pages/contact';
import About from '../pages/about';


const Header = () => {
    return (
        <HeaderContainer>

        <HeaderImg>
            <img src='./images/header-pic2.jpg' alt='Header Image. Photo by <a href="https://unsplash.com/de/@towfiqu999999?utm_source=unsplash&utm_medium=referral&utm_content=creditCopyText">Towfiqu barbhuiya</a> on <a href="https://unsplash.com/photos/n-7HTOiJPso?utm_source=unsplash&utm_medium=referral&utm_content=creditCopyText">Unsplash</a>'/>
        </HeaderImg>

        <TopContainer>
            <NavbarContainer>
            <LogoContainer>
                <img src='./images/Logo.png' alt='twokay logo' />	
            </LogoContainer>
            <NavContainer>
                <Nav>
                    <Router className="Navmenu">
                        <Routes>
                            <Route className ="Link" path='./home' element={<Home/>}>HOME</Route>
                            <Route className ="Link" path='./products' element={<Product/>}>PRODUCTS</Route>
                            <Route className ="Link" path='./contact' element= {<Contact/>}>CONTACT</Route>
                            <Route className ="Link" path='./about' element = {<About />}>ABOUT</Route>
                        </Routes>
                    </Router>
                </Nav>
            </NavContainer>
            </NavbarContainer>
        </TopContainer>
        
        </HeaderContainer>
    )
};

const HeaderContainer = styled.div`

`
const HeaderImg = styled.div`
max-width: 100vw;
height: 650px;

img {
    width:100%;
    height:100%;
    object-fit:cover;
}

`
const TopContainer = styled.div`
position: absolute;
top:0;
width: 100%;
`


const NavbarContainer = styled.div`

`


const Nav = styled.div`

`

const NavMenu = styled.div`
 

