import React from 'react';
import styled from 'styled-components'
import {NavLink} from "react-router-dom";  

const Navbar = (props) => {
    return (
<TopContainer>
            <NavbarContainer>
            <LogoContainer>
                <h2>TWOKAY CHEMICALS</h2>
                <img src='./images/Logo.png' alt='twokay logo' />	
            </LogoContainer>
            <NavContainer>
                <Nav>
                    <NavMenu> 
                        <NavLink className="Link" to="/" activestyle><span>HOME</span></NavLink>
                        <NavLink className="Link" to="/products" activestyle><span>PRODUCTS</span></NavLink>
                        <NavLink className="Link" to="/contact" activestyle><span>CONTACT US</span></NavLink>
                        <NavLink className="Link" to="/about" activestyle><span>ABOUT</span></NavLink>
                    </NavMenu>
                </Nav>
            </NavContainer>
            </NavbarContainer>
        </TopContainer>
    )
};


const TopContainer = styled.div`
position: absolute;
top:0;
width: 100%;

`

const NavbarContainer = styled.div`
display:flex;
flex-direction: row;
justify-content: space-between;`

const Nav = styled.div`
display: flex;
height: 85px;
font-family: 'Poppins', sans-serif;
font-weight: 500;
font-size: 1.2rem;
justify-content: space-between;
padding: 20px 50px 50px 20px;
z-index: 12;
`

const NavMenu = styled.div`
  display: flex;
  align-items: center;
  margin-right: -24px;
  /* Second Nav */
  /* margin-right: 24px; */
  /* Third Nav */
  /* width: 100vw;
white-space: nowrap; */
  @media screen and (max-width: 768px) {
    display: none;
  }

.Link {
    color: #033B4A;
    display: inline-block;
    align-items: center;
    text-decoration: none;
    padding: 0 1rem;
    height: 100%;
    cursor: pointer;
    
    &.active {
      color: #193874;
        span {
            border-bottom-style: solid;
            border-bottom-width: 2px;
            padding-bottom: .1rem;
            padding-top: .5rem;
            transition: width 0.3s ease-in-out;
        }
    }
    &.hover {

    }
}

`
const NavContainer = styled.div`

`
const LogoContainer = styled.div`
padding: 0 0 0 40px;
display: flex;
flex-direction: row;
height: 550px;
background-color: rgb(45,169,92, .9);
width: 40%;
img {
    background: transparent;
    max-width: 100px;
    max-height: 100px;

}

h2{
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 20px;
    color:white;
}

`

export default Navbar;
