import React from "react";
import styled from 'styled-components';


const Body = (props) => {
    console.log(props);;

    return (
        <Bodycard>
        
            <ImageContainer>
                <h3>{props.title}</h3>
                <p>{props.description}</p>
                {/* <img src={props.img } alt="" /> */}
            </ImageContainer>
            <Content>
                
                <h2><span>{props.headingPartOne}</span>{props.headingPartTwo}</h2>
                <p>{props.content}</p>         
            </Content>
            

        </Bodycard>
    )
}

const Bodycard = styled.div`
display: flex;
flex-direction: row;
margin: 100px 200px;



img {
    margin-top: 50px;
    max-width:400px;
    height:auto;
  
}

    
`
const Content = styled.div`
    width: 60%;

span {
    padding-bottom: 10px;
    border-bottom-style: solid;
    border-bottom-width: 0.5px;
    width: fit-content;
    
}

color: #033B4A;

h3 {
   font-family: Playfair Display,serif;
   font-weight: 300;
   line-height: 40px;
   font-size: 30px;
   color: #2a2a2a;
  
}

h2 {
    font-family: 'Poppins', sans-serif;
    font-size: 35px;
    font-weight: 200;
    line-height: 35px;
    color: rgb(59, 94, 103);
}

P {
 
    font-family: 'Poppins', sans-serif;
    width: 100%;
    font-size:15px;
    font-weight: 400;
    line-height: 27px;
    text-align: justify;

}
`
const ImageContainer = styled.div`
width: 50%;

h3 {
       font-family: Playfair Display,serif;
       font-weight: 300;
       line-height: 48px;
       font-size: 45px;
       color: #2a2a2a;
      
    }
`


export default Body;
