import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <NavContainer>
      <Nav className={`navbar ${isOpen ? 'open' : ''}`}>
        <div className="companyName-container">
          <h3 className='companyName'>Twokay Chemicals</h3>
        </div>
        <MobileMenu onClick={toggleNavbar}>
          <div className={`bar ${isOpen ? 'open' : ''}`}></div>
          <div className={`bar ${isOpen ? 'open' : ''}`}></div>
          <div className={`bar ${isOpen ? 'open' : ''}`}></div>
        </MobileMenu>
        <NavItems className={`nav-items ${isOpen ? 'open' : ''}`}>
          <NavLink className="Link" to="/"><span>HOME</span></NavLink>
          <NavLink className="Link" to="/products"><span>PRODUCTS</span></NavLink>
          <NavLink className="Link" to="/contact"><span>CONTACT US</span></NavLink>
          <NavLink className="Link" to="/about"><span>ABOUT</span></NavLink>
        </NavItems>
        <SearchContainer>
          <input type="text" placeholder="Search" className="search-input" />
          <button className="search-button">Search</button>
        </SearchContainer>
      </Nav>
      {/* Your content container */}
      <ContentContainer>
        {/* Your content here */}
      </ContentContainer>
    </NavContainer>
  );
};

const NavContainer = styled.div`
  position: relative;
`;

const Nav = styled.div`
  background-color: #033B4A;
  color: white;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  z-index: 1000;
  font-family: 'Poppins', sans-serif;

  &.open {
    /* ... (existing styles) ... */
  }

  /* ... (media query styles) ... */
`;

const MobileMenu = styled.div`
  display: flex;
  flex-direction: column;
  cursor: pointer;
  color: #033B4A;
  position: absolute;
  top: 20px;
  right: 20px;
  z-index: 1100;

  .bar {
    width: 25px;
    height: 3px;
    background-color: #033B4A;
    margin: 2px 0;
    transition: 0.2s;
  }

  &.open .bar:nth-child(1) {
    transform: rotate(-45deg) translate(4px, 6px);
  }

  &.open .bar:nth-child(2) {
    opacity: 0;
  }

  &.open .bar:nth-child(3) {
    transform: rotate(45deg) translate(4px, -6px);
  }
`;

const NavItems = styled.ul`
  /* ... (existing styles) ... */
`;

const SearchContainer = styled.div`
  /* ... (existing styles) ... */
`;

const ContentContainer = styled.div`
  /* Adjust the top margin to create space for the open mobile menu */
  margin-top: 85px; /* Adjust the value as needed */
`;

export default Navbar;
